import classes from "./Checkout.module.css";
import { useRef,useState } from "react";

const isEmpty = (value) => value.trim() !== "";
const isNot6Char = (value) => value.trim().length === 6;

const Checkout = (props) => {
    const[formValidity,setFormValidity]=useState()
  const nameRef = useRef();
  const streetRef = useRef();
  const postalRef = useRef();
  const cityRef = useRef();

  const confirmHandler = (event) => {
    event.preventDefault();
    const enteredName = nameRef.current.value;
    const enteredStreet = streetRef.current.value;
    const enteredPostal = postalRef.current.value;
    const enteredCity = cityRef.current.value;

    const enteredNameIsValid = !isEmpty(enteredName);
    const enteredStreetIsValid = !isEmpty(enteredStreet);
    const enteredPostalIsValid = isNot6Char(enteredPostal);
    const enteredCityIsValid = !isEmpty(enteredCity);

    setFormValidity({
        name:enteredNameIsValid,
        street:enteredStreetIsValid,
        city:enteredCityIsValid,
        postalCode:enteredPostalIsValid
    })

    const formIsValid =
      enteredNameIsValid &&
      enteredCityIsValid &&
      enteredPostalIsValid &&
      enteredStreetIsValid;
      if (!formIsValid) {
        return ;
      }
  };

  const nameControlClasses=`${classes.control} ${formValidity.name ? '':classes.invalid}`;
  const streetControlClasses=`${classes.control} ${formValidity.street ? '':classes.invalid}`;
  const cityControlClasses=`${classes.control} ${formValidity.city ? '':classes.invalid}`;
  const postalControlClasses=`${classes.control} ${formValidity.postal ? '':classes.invalid}`;

  
  return (
    <from onSubmit={confirmHandler}>
      <div className={nameControlClasses}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" ref={nameRef} />
        {!formValidity.name && <p>Please Enter a Valid input !</p>}
      </div>
      <div className={streetControlClasses}>
        <label htmlFor="street">Street</label>
        <input type="text" id="stret" ref={streetRef} />
        {!formValidity.street && <p>Please Enter a Valid input !</p>}
      </div>
      <div className={cityControlClasses}>
        <label htmlFor="postal">Postal Code</label>
        <input type="text" id="postal" ref={postalRef} />
        {!formValidity.postalCode && <p>Please Enter a Valid input (6 Characters Long)!</p>}
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" ref={cityRef} />
        {!formValidity.city && <p>Please Enter a Valid input !</p>}
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </from>
  );
};

export default Checkout;
